addpath(pwd)function table = dividedtable (nodes, values)  table = nan(size(values)(2));  table(:,1) = values;  for col=2:length(values)    table(1:end-col+1,col) = (table(2:end-col+2,col-1) - table(1:end-col+1,col-1)) ./ (nodes(col:end) - nodes(1:end-col+1))';  endforendfunctionfunction N = newton(nodes, values, x)  table = dividedtable(nodes, values);    lx = length(x);  p = ones(1,lx);  s = table(1,1) * ones(1,lx);    for j=1:lx    for i=1:length(nodes) - 1            p(j) = p(j) * (x(j) - nodes(i));      s(j) = s(j) + p(j) * table(1, i+1);    endfor
  endfor
    N=s;  endfunctionnodes = [1 2 3 4 5];values = [22 23 25 30 28];printf("2.5 -> %d\n", newton(nodes, values, 2.5))x = 1:0.01:5;newtonpoly = newton(nodes, values, x);figure(1)title("Problem 2")hold onplot(nodes, values, 'x');plot(x, newtonpoly);